-- ------------------------------------------------------- Table `restaurant`.`categories`-- -----------------------------------------------------CREATE TABLE IF NOT EXISTS `restaurant`.`categories` (  `id` VARCHAR(36) CHARACTER SET 'utf8' COLLATE 'utf8_general_ci' NOT NULL,  `name` VARCHAR(191) NOT NULL,  `description` VARCHAR(255) NULL,  `createdAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,  `updatedAt` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  PRIMARY KEY (`id`),  UNIQUE INDEX `id_UNIQUE` (`id` ASC) VISIBLE)ENGINE = InnoDB;